mapper.select:
SELECT DISTINCT p.id, p.title, p.content, p.price, ph.upload_file_name, ph.thumbnail, c.* FROM product p LEFT JOIN product_photos ph ON p.id = ph.product_id AND ph.thumbnail IS NOT NULL LEFT JOIN city_code c ON p.city_code_id = c.id WHERE 1 = 1
AND (title LIKE CONCAT('%', #{searchText}, '%') OR content LIKE CONCAT('%', #{searchText}, '%'))
AND city_code_id = #{cityCodeId}
ORDER BY p.id DESC LIMIT #{limit} OFFSET #{offset}

select count(*) from product as p LEFT JOIN product_photos ph ON p.id = ph.product_id AND ph.thumbnail IS NOT NULL LEFT JOIN city_code c ON p.city_code_id = c.id where 1 = 1
and title like CONCAT('%', #{searchText}, '%') or content like CONCAT('%', #{searchText} , '%')
and city_code_id = #{cityCodeId}
